import { Fragment, useState } from "react";
import UserMenu from "../UserMenu/UserMenu";
import styles from "./Header.module.css";

const Header = () => {
  const [isUserMenuVisible, setUserMenuVisible] = useState(false);

  const handleUserMenu = () => {
    setUserMenuVisible(!isUserMenuVisible);
  }

  return (
    <header className={styles.header}>
      <h1 className={styles.projectName}>Awesome Kanban Board</h1>
      <div className={styles.userMenu}>
        <div className={styles.user} onClick={handleUserMenu}>
          <span className={styles.userImg}></span>
          {isUserMenuVisible && (
            <p className={styles.arrow}>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-chevron-up" viewBox="0 0 16 16">
                <path fillRule="evenodd" d="M7.646 4.646a.5.5 0 0 1 .708 0l6 6a.5.5 0 0 1-.708.708L8 5.707l-5.646 5.647a.5.5 0 0 1-.708-.708l6-6z"/>
              </svg>
            </p>
          )}
          {!isUserMenuVisible && (
            <p className={styles.arrow}>
              <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-chevron-down" viewBox="0 0 16 16">
                <path fillRule="evenodd" d="M1.646 4.646a.5.5 0 0 1 .708 0L8 10.293l5.646-5.647a.5.5 0 0 1 .708.708l-6 6a.5.5 0 0 1-.708 0l-6-6a.5.5 0 0 1 0-.708z"/>
              </svg>
            </p>
          )}
        </div>
        {isUserMenuVisible ? <UserMenu setUserMenuVisible={setUserMenuVisible}/> : null}
      </div>
    </header>
  );
};

export default Header;
